let burgerMenu = document.querySelector('.menu-burger__header');
let header =  document.querySelector('.header');

$(document).ready(function() {
    burgerMenu.addEventListener("click", function(){
        console.log(burgerMenu);

        burgerMenu.classList.contains('open-menu') ?
        burgerMenu.classList.remove('open-menu') :
        burgerMenu.classList.add('open-menu');

        header.classList.contains('open-menu') ?
        header.classList.remove('open-menu') :
        header.classList.add('open-menu');
    });
    $(function() {
        //BEGIN
        $(".js-faq-title").on("click", function(e) {
            console.log("test");
    
            e.preventDefault();
            var $this = $(this);
    
            if (!$this.hasClass("faq__active")) {
                $(".js-faq-content").slideUp(800);
                $(".js-faq-title").removeClass("faq__active");
                $('.js-faq-rotate').removeClass('faq__rotate');
            }
    
            $this.toggleClass("faq__active");
            $this.next().slideToggle();
            $('.js-faq-rotate',this).toggleClass('faq__rotate');
        });
        //END
        
    });
});

$.ajax({
    type: 'POST',
    dataType: 'json',
    url: 'https://idalpcapital.com/api/getWhitePapers.php',

    headers: {
        'Content-Type': 'application/json'
    },
    xhrFields: {
        withCredentials: false 
    },
    success: function(sityData) {
        console.log(sityData.data[0]);
        $.each(sityData.data, function(index, value){
            // console.log(sityData.data[index].Description);
            $('.cityList').append("<li class='search-span'>"+ sityData.data[index].Description + "</li>");
        });
    },
});

